import React from 'react';
import { useAuth } from '../context/AuthContext';

const StudentPage = () => {
  const { user, logout } = useAuth();

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-900">Student Dashboard</h1>
            <button
              onClick={logout}
              className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
            >
              Logout
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-blue-50 p-6 rounded-lg">
              <h2 className="text-lg font-semibold text-blue-900 mb-2">Welcome, {user?.firstName}!</h2>
              <p className="text-blue-700">Student ID: {user?.studentId || 'Not assigned'}</p>
              <p className="text-blue-700">Email: {user?.email}</p>
            </div>

            <div className="bg-green-50 p-6 rounded-lg">
              <h2 className="text-lg font-semibold text-green-900 mb-2">Your Information</h2>
              <p className="text-green-700">Name: {user?.firstName} {user?.lastName}</p>
              <p className="text-green-700">Role: {user?.role}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentPage;
