import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';

const RegisterPage = () => {
  const navigate = useNavigate();
  const { register, isAuthenticated } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'student',
    major: '',
    department: '',
    academicYear: '',
    studentId: '',
    phone: '',
    address: '',
    dateOfBirth: '',
    gender: '',
    grades: [],
    // Teacher-specific fields
    specialization: '',
    experience: '',
    qualifications: '',
    officeHours: '',
    // Admin-specific fields
    adminLevel: '',
    permissions: []
  });

  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleGradeChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      grades: prev.grades.map((grade, i) =>
        i === index ? { ...grade, [field]: value } : grade
      )
    }));
  };

  const addGrade = () => {
    setFormData(prev => ({
      ...prev,
      grades: [...prev.grades, {
        subject: '',
        grade: '',
        semester: '',
        year: '',
        credits: '',
        id: Date.now()
      }]
    }));
  };

  const removeGrade = (index) => {
    setFormData(prev => ({
      ...prev,
      grades: prev.grades.filter((_, i) => i !== index)
    }));
  };

  const handleRoleChange = (role) => {
    setFormData(prev => ({
      ...prev,
      role,
      // Reset role-specific fields when changing role
      major: role === 'student' ? prev.major : '',
      department: role === 'student' || role === 'teacher' ? prev.department : '',
      academicYear: role === 'student' ? prev.academicYear : '',
      studentId: role === 'student' ? prev.studentId : '',
      specialization: role === 'teacher' ? prev.specialization : '',
      experience: role === 'teacher' ? prev.experience : '',
      qualifications: role === 'teacher' ? prev.qualifications : '',
      officeHours: role === 'teacher' ? prev.officeHours : '',
      adminLevel: role === 'admin' ? prev.adminLevel : '',
      permissions: role === 'admin' ? prev.permissions : []
    }));
  };

  const handleBlur = (field) => {
    setTouched(prev => ({
      ...prev,
      [field]: true
    }));
    validateField(field);
  };

  const validateField = (field) => {
    const newErrors = { ...errors };

    switch (field) {
      case 'firstName':
        if (!formData.firstName.trim()) {
          newErrors.firstName = 'First name is required';
        } else if (formData.firstName.length < 2) {
          newErrors.firstName = 'First name must be at least 2 characters';
        } else {
          delete newErrors.firstName;
        }
        break;

      case 'lastName':
        if (!formData.lastName.trim()) {
          newErrors.lastName = 'Last name is required';
        } else if (formData.lastName.length < 2) {
          newErrors.lastName = 'Last name must be at least 2 characters';
        } else {
          delete newErrors.lastName;
        }
        break;

      case 'email':
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!formData.email.trim()) {
          newErrors.email = 'Email is required';
        } else if (!emailRegex.test(formData.email)) {
          newErrors.email = 'Please enter a valid email address';
        } else {
          delete newErrors.email;
        }
        break;

      case 'password':
        if (!formData.password) {
          newErrors.password = 'Password is required';
        } else if (formData.password.length < 8) {
          newErrors.password = 'Password must be at least 8 characters';
        } else if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.password)) {
          newErrors.password = 'Password must contain at least one uppercase letter, one lowercase letter, and one number';
        } else {
          delete newErrors.password;
        }
        break;

      case 'confirmPassword':
        if (!formData.confirmPassword) {
          newErrors.confirmPassword = 'Please confirm your password';
        } else if (formData.password !== formData.confirmPassword) {
          newErrors.confirmPassword = 'Passwords do not match';
        } else {
          delete newErrors.confirmPassword;
        }
        break;

      case 'studentId':
        if (formData.role === 'student' && !formData.studentId.trim()) {
          newErrors.studentId = 'Student ID is required';
        } else {
          delete newErrors.studentId;
        }
        break;

      case 'major':
        if (formData.role === 'student' && !formData.major.trim()) {
          newErrors.major = 'Major is required';
        } else {
          delete newErrors.major;
        }
        break;

      case 'department':
        if ((formData.role === 'student' || formData.role === 'teacher') && !formData.department.trim()) {
          newErrors.department = 'Department is required';
        } else {
          delete newErrors.department;
        }
        break;

      case 'academicYear':
        if (formData.role === 'student' && !formData.academicYear) {
          newErrors.academicYear = 'Academic year is required';
        } else {
          delete newErrors.academicYear;
        }
        break;

      case 'specialization':
        if (formData.role === 'teacher' && !formData.specialization.trim()) {
          newErrors.specialization = 'Specialization is required';
        } else {
          delete newErrors.specialization;
        }
        break;

      case 'experience':
        if (formData.role === 'teacher' && !formData.experience.trim()) {
          newErrors.experience = 'Experience is required';
        } else {
          delete newErrors.experience;
        }
        break;

      case 'adminLevel':
        if (formData.role === 'admin' && !formData.adminLevel) {
          newErrors.adminLevel = 'Administrative level is required';
        } else {
          delete newErrors.adminLevel;
        }
        break;

      default:
        break;
    }

    setErrors(newErrors);
  };

  const validateStep = (step) => {
    const newErrors = {};

    if (step === 1) {
      if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
      if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
      if (!formData.email.trim()) newErrors.email = 'Email is required';
      if (!formData.password) newErrors.password = 'Password is required';
      if (!formData.confirmPassword) newErrors.confirmPassword = 'Please confirm your password';
      if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
    }

    if (step === 2) {
      if (formData.role === 'student') {
        if (!formData.studentId.trim()) newErrors.studentId = 'Student ID is required';
        if (!formData.major.trim()) newErrors.major = 'Major is required';
        if (!formData.department.trim()) newErrors.department = 'Department is required';
        if (!formData.academicYear) newErrors.academicYear = 'Academic year is required';
      } else if (formData.role === 'teacher') {
        if (!formData.department.trim()) newErrors.department = 'Department is required';
        if (!formData.specialization.trim()) newErrors.specialization = 'Specialization is required';
        if (!formData.experience.trim()) newErrors.experience = 'Experience is required';
      }
    }

    if (step === 3 && formData.role === 'admin') {
      if (!formData.adminLevel) newErrors.adminLevel = 'Administrative level is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateStep(currentStep)) {
      return;
    }

    setIsLoading(true);

    try {
      const result = await register(formData);
      if (result.success) {
        toast.success('Registration successful! Welcome to Syriana.');
        navigate('/dashboard');
      } else {
        toast.error(result.message || 'Registration failed');
      }
    } catch (error) {
      console.error('Registration error:', error);
      toast.error('Registration failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const getRoleDisplayName = (role) => {
    switch (role) {
      case 'student': return 'Student';
      case 'teacher': return 'Teacher';
      case 'admin': return 'Administrator';
      default: return 'User';
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case 'student':
        return (
          <svg className="h-8 w-8 text-emerald-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
          </svg>
        );
      case 'teacher':
        return (
          <svg className="h-8 w-8 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
          </svg>
        );
      case 'admin':
        return (
          <svg className="h-8 w-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
          </svg>
        );
      default:
        return null;
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'student': return 'emerald';
      case 'teacher': return 'blue';
      case 'admin': return 'red';
      default: return 'gray';
    }
  };

  const renderStepIndicator = () => {
    const totalSteps = formData.role === 'admin' ? 4 : 3;
    return (
      <div className="flex items-center justify-center mb-8">
        {Array.from({ length: totalSteps }, (_, i) => i + 1).map((step) => (
          <div key={step} className="flex items-center">
            <div
              className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-semibold transition-all duration-300 ${
                currentStep >= step
                  ? `bg-${getRoleColor(formData.role)}-500 text-white shadow-lg`
                  : 'bg-gray-200 text-gray-600'
              }`}
            >
              {step}
            </div>
            {step < totalSteps && (
              <div
                className={`w-16 h-1 mx-3 rounded transition-all duration-300 ${
                  currentStep > step ? `bg-${getRoleColor(formData.role)}-500` : 'bg-gray-200'
                }`}
              />
            )}
          </div>
        ))}
      </div>
    );
  };

  const renderStep1 = () => {
    const color = getRoleColor(formData.role);
    return (
      <div className="space-y-8">
        <div className="text-center">
          <div className={`mx-auto h-20 w-20 bg-gradient-to-br from-${color}-500 to-${color}-600 rounded-2xl flex items-center justify-center mb-6 shadow-lg transform hover:scale-105 transition-transform duration-200`}>
            {getRoleIcon(formData.role)}
          </div>
          <h2 className="text-3xl font-bold text-gray-900 mb-2">Join Syriana as {getRoleDisplayName(formData.role)}</h2>
          <p className="text-lg text-gray-600">Create your account and start your journey</p>
        </div>

        {/* Role Selection */}
        <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-6">
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700 mb-4">
              I am registering as:
            </label>
            <div className="grid grid-cols-3 gap-4">
              {['student', 'teacher', 'admin'].map((role) => (
                <button
                  key={role}
                  type="button"
                  onClick={() => handleRoleChange(role)}
                  className={`p-4 rounded-xl border-2 transition-all duration-200 ${
                    formData.role === role
                      ? `border-${color}-500 bg-${color}-50 text-${color}-700 shadow-md`
                      : 'border-gray-200 bg-white text-gray-600 hover:border-gray-300'
                  }`}
                >
                  <div className="flex flex-col items-center space-y-3">
                    <div className={`p-3 rounded-lg ${formData.role === role ? `bg-${color}-100` : 'bg-gray-100'}`}>
                      {getRoleIcon(role)}
                    </div>
                    <span className="text-sm font-medium">{getRoleDisplayName(role)}</span>
                  </div>
                </button>
              ))}
            </div>
          </div>

          {/* Basic Information */}
          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="firstName" className="block text-sm font-semibold text-gray-700 mb-2">
                  First Name *
                </label>
                <input
                  id="firstName"
                  name="firstName"
                  type="text"
                  required
                  className={`w-full px-4 py-3 border rounded-xl shadow-sm focus:ring-2 focus:ring-${color}-500 focus:border-transparent transition-all duration-200 ${
                    errors.firstName && touched.firstName ? 'border-red-300 focus:ring-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter your first name"
                  value={formData.firstName}
                  onChange={handleChange}
                  onBlur={() => handleBlur('firstName')}
                />
                {errors.firstName && touched.firstName && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    {errors.firstName}
                  </p>
                )}
              </div>

              <div>
                <label htmlFor="lastName" className="block text-sm font-semibold text-gray-700 mb-2">
                  Last Name *
                </label>
                <input
                  id="lastName"
                  name="lastName"
                  type="text"
                  required
                  className={`w-full px-4 py-3 border rounded-xl shadow-sm focus:ring-2 focus:ring-${color}-500 focus:border-transparent transition-all duration-200 ${
                    errors.lastName && touched.lastName ? 'border-red-300 focus:ring-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter your last name"
                  value={formData.lastName}
                  onChange={handleChange}
                  onBlur={() => handleBlur('lastName')}
                />
                {errors.lastName && touched.lastName && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <svg className="h-4 w-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    {errors.lastName}
                  </p>
                )}
              </div>
            </div>

